DROP TABLE IF EXISTS transaction
;
CREATE TABLE transaction
(
	id BIGINT NOT NULL AUTO_INCREMENT,
	date TIMESTAMP NOT NULL,
	type CHAR(3) COMMENT 'Transaction type ',
	clerkId INTEGER NOT NULL COMMENT 'Pints to the clerk',
	deptId INTEGER,
	pluId INTEGER COMMENT 'Pints to Price Lookup definition',
	quantity INTEGER,
	descr VARCHAR(50),
	price DECIMAL(5,2),
	tax DECIMAL(5,2),
	amount DECIMAL(10,2),
	PRIMARY KEY (id)
) 
;


